mapper.select:
select board_num boardNum, user_id userId, title, content, answer, separation, answer_state answerState, reporting_date reportingDate
from boards
where board_num = #{boardNum}

select board_num boardNum, user_id userId, title, content, answer, separation, answer_state answerState, reporting_date reportingDate
from boards
where user_id = #{userId}

mapper.update:
update boards set
answer = #{answer},
answer_state=#{answerState}
where board_num = #{boardNum}
